import { Image, Row, Col, Input, Button, List, Space, Card, Divider, Tag } from "antd";
import { SearchOutlined, EnvironmentFilled, TeamOutlined } from '@ant-design/icons';
import React from "react";
import { AxiosResponse } from "axios";
import { getMyLearningPagination } from "../../repository/MyLearningRepository";
import { dataList } from "../../components/data/dataList";

interface MyLearningProps { }
interface MyLearningState {
    // data: any[];
    // pagination: any;
}

// const { CheckableTag } = Tag;

// const tagsData = ['All', 'Applied', 'On Progress', 'Completed', 'Bookmark'];

class MyLearning extends React.Component<MyLearningProps, MyLearningState>{

    typeColor(class_type: string) {
        if (class_type == "Training") {
            return "palegreen";
        } else if (class_type == "Workshop") {
            return "#1890ff";
        } else {
            return "white";
        }
    }

    typeTextColor(class_type: string) {
        if (class_type == "Training") {
            return "black";
        } else if (class_type == "Workshop") {
            return "white";
        } else {
            return "black";
        }
    }

    // handleChange(tag: any, checked: any) {
    //     const { selectedTags } = this.state;
    //     const nextSelectedTags = checked ? [...selectedTags, tag] : selectedTags.filter(t => t !== tag);
    //     console.log('Status dipilih: ', nextSelectedTags);
    //     this.setState({ selectedTags: nextSelectedTags });
    // }

    // coba filter
    handleClick = (e: any) => {
        let dataCopy;
        if (e.target.value === 'All') {
            dataCopy = this.state.data;
        } else {
            dataCopy = this.state.data.filter((item) => item.status === e.target.value)
        }

        this.setState({
            dataCopy: dataCopy,
        });
    }

    state = {
        data: dataList,
        dataCopy: dataList,
        pagination: {
            page: 1,
            total: 0,
        },
        selectedTags: ['All'],
    };

    componentDidMount() {
        this.getData();
    }

    getData() {
        getMyLearningPagination().then((res: AxiosResponse<any>) => {
            this.setState({
                data: res.data.data,
                pagination: {
                    page: res.data.page,
                    total: res.data.total,
                },
            });
            console.log(res);
            console.log(this.state.data);
        });
    }

    render() {
        const { selectedTags } = this.state;
        return (
            <>
                <div>
                    <Row>
                        <Col span={24}>
                            <Row justify="space-between">
                                <Col>
                                    <h3>My Learning</h3>
                                </Col>
                                <Col>
                                    <Space>
                                        <Input className="searchBox" placeholder="Masukkan kata kunci" suffix={<SearchOutlined />} allowClear />
                                        <Button className="searchButton" type="primary">Search Class</Button>
                                    </Space>
                                </Col>
                            </Row>
                            <Divider />
                            <Row>
                                <div className="buttonFilter">
                                    {/* {tagsData.map(tag => (
                                        <CheckableTag
                                            key={tag}
                                            checked={selectedTags.indexOf(tag) > -1}
                                            onChange={checked => this.handleChange(tag, checked)}
                                        >
                                            {tag}
                                        </CheckableTag>
                                    ))} */}

                                    <Button value="All" onClick={this.handleClick}>
                                        All
                                    </Button>
                                    <Button value="Applied" onClick={this.handleClick}>
                                        Applied
                                    </Button>
                                    <Button value="On-Progress" onClick={this.handleClick}>
                                        On-Progress
                                    </Button>
                                    <Button value="Completed" onClick={this.handleClick}>
                                        Completed
                                    </Button>
                                    <Button value="Bookmark" onClick={this.handleClick}>
                                        Bookmark
                                    </Button>
                                </div>
                            </Row>
                            <Row>
                                <List style={{ width: "100%" }}
                                    itemLayout="horizontal"
                                    // dataSource={dataList}
                                    dataSource={this.state.data}
                                    pagination={{
                                        onChange: (e) => {
                                            console.log(e);
                                        },
                                        defaultCurrent: 1,
                                        pageSize: 3,
                                    }}
                                    renderItem={(item: any) => (
                                        <List.Item>
                                            <Card className="cardContainer">
                                                <Row gutter={[20, 0]}>
                                                    <Col span={4}>
                                                        <Image className="imagePreview"
                                                            preview={false}
                                                            src={item.image}
                                                        />
                                                    </Col>
                                                    <Col span={20}>
                                                        <Row justify="space-between">
                                                            <Col>
                                                                <Space>
                                                                    <Tag className="class_typeTag" style={{ backgroundColor: this.typeColor(item.class_type), color: this.typeTextColor(item.class_type) }}>{item.class_type}</Tag>
                                                                    <h2>{item.class_title}</h2>
                                                                </Space>
                                                            </Col>
                                                            <Col>
                                                                <TeamOutlined /> {item.participant_count} participant
                                                            </Col>
                                                        </Row>
                                                        <Divider />
                                                        <Row justify="space-between">
                                                            <Col>
                                                                <Row gutter={10}>
                                                                    <Col>
                                                                        Applied On
                                                                        <br />
                                                                        Posted On
                                                                        <br />
                                                                        Will Start On
                                                                        <br />
                                                                        Access Link
                                                                    </Col>
                                                                    <Col>
                                                                        :
                                                                        <br />
                                                                        :
                                                                        <br />
                                                                        :
                                                                        <br />
                                                                        :
                                                                    </Col>
                                                                    <Col>
                                                                        {item.applied_on}
                                                                        <br />
                                                                        {item.posted_on}
                                                                        <br />
                                                                        {item.will_start_on}
                                                                        <br />
                                                                        <a href={item.access_link}>{item.access_link}</a>
                                                                    </Col>
                                                                </Row>
                                                            </Col>
                                                            <Col>
                                                                <Tag>{item.status}</Tag>
                                                            </Col>
                                                        </Row>
                                                    </Col>
                                                </Row>
                                            </Card>
                                        </List.Item>
                                    )}
                                />
                            </Row>
                        </Col>
                    </Row>
                </div>
            </>
        );
    }
}

export default MyLearning;
